mapper.select:
SELECT * 
FROM OWNITEM 
WHERE O_MNO=#{mno}

SELECT COUNT(*) 
FROM DELIVERY 
WHERE D_MNO = #{mno}

mapper.update:
UPDATE MEMBER SET MPRNEW = #{mprNew}
WHERE MNO = #{mno}
AND MTAKESTATUS IN (1,2)

UPDATE MEMBER SET MPR = #{mpr}
WHERE MNO = #{mno}
AND MTAKESTATUS = 3

mapper.select:
SELECT Q.QNO, Q.QDATE, Q.QTITLE, Q.QCONTENT, Q.QANSWER, Q.QWRITER, Q.Q_MNO 
FROM QNA Q, MEMBER M 
WHERE Q.Q_MNO = M.MNO 
AND Q.QWRITER = 'H' 
AND Q.QNO = #{qno}
AND Q.Q_MNO = #{q_mno}

mapper.update:
UPDATE ATTACHMENT SET AORIGINNAME = #{origin}, ACHANGENAME = #{change}, ADATE = SYSDATE 
WHERE A_MNO = #{mno} 
AND ADOCTYPE = '1'

UPDATE ATTACHMENT SET AORIGINNAME = #{origin}, ACHANGENAME = #{change}, ADATE = SYSDATE 
WHERE A_MNO = #{mno} 
AND ADOCTYPE = '2'

mapper.select:
SELECT FH_MNO_GIVE 
FROM FUNDHISTORY 
WHERE FH_FNO = #{fhFno}

SELECT FTYPE 
FROM FUNDING 
WHERE FNO = #{fno}

SELECT COUNT(*) 
FROM LETTER 
WHERE LE_MNO_TAKE = #{leMnoTake} 
AND LE_MNO_GIVE = #{leMnoGive} 
AND LETYPE = #{leType}
